import React, { useState } from 'react';
import { Card, Form, Alert, Container, Row, Col, Button } from 'react-bootstrap';
import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import { Stack } from &quot;@mui/joy&quot;;
import './headshot.scss';
import AWS from 'aws-sdk';

// Configure AWS SDK
const awsConfig = {
    accessKeyId: 'AKIAWYBABBK5D33BSLAF',
    secretAccessKey: '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\n',
    region: 'us-east-1'
}
AWS.config.update(awsConfig);

function Headshot() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [loading, setLoading] = useState(false);
    const [resultImages, setResultImages] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    const onImageSelect = (event) =&gt; {
        setErrorMessage('');
        const files = Array.from(event.target.files);
        // Limit the number of selected files to 20
        if (files.length &gt; 20) {
            setErrorMessage('You can only upload up to 20 images.');
            return;
        }
        setSelectedFiles(files);
    };

    const handleSubmit = async () =&gt; {
        // Check if the number of selected files exceeds 20
        if (selectedFiles.length &gt; 20) {
            setErrorMessage('You can only upload up to 20 images.');
            return;
        }

        setLoading(true);
        setErrorMessage('');

        const formData = new FormData();
        selectedFiles.forEach((file) =&gt; {
            formData.append('images', file);
        });

        try {
            const response = await fetch('YOUR_BACKEND_ENDPOINT', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            // Assuming the backend returns processed images
            setResultImages(data.images);
        } catch (error) {
            setErrorMessage('Error processing images. Please try again later.');
        } finally {
            setLoading(false);
        }
    };




    return (
        &lt;Stack sx={{ minHeight: '100vh' }}&gt;
            &lt;Navbar/&gt;
            &lt;div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: '100vh', // Ensures the container takes up the full height of the viewport
            }}&gt;
                &lt;div id=&quot;headshot&quot; style={{maxWidth: '800px', width: '100%'}}&gt;
                    &lt;div id=&quot;steps-container&quot; style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}&gt;
                        &lt;div id=&quot;step1&quot; className=&quot;step-card&quot;&gt;
                            &lt;h3&gt;Step 1: Upload 15-20 high-quality images&lt;/h3&gt;
                        &lt;/div&gt;
                        &lt;br/&gt;
                        &lt;div id=&quot;step2&quot; className=&quot;step-card&quot;&gt;
                            &lt;h3&gt;Step 2: Click Submit&lt;/h3&gt;
                        &lt;/div&gt;
                        &lt;br/&gt;
                        &lt;div id=&quot;step3&quot; className=&quot;step-card&quot;&gt;
                            &lt;h3&gt;Step 3: Wait for Results&lt;/h3&gt;
                        &lt;/div&gt;
                        &lt;br/&gt;
                        &lt;div id=&quot;step4&quot; className=&quot;step-card&quot;&gt;
                            &lt;h3&gt;Step 4: Download your AI headshots&lt;/h3&gt;
                        &lt;/div&gt;
                    &lt;/div&gt;
                    &lt;Card style={{margin: 'auto', width: '600px', maxHeight: '600px'}}&gt; {/* Initial dimensions */}
                        &lt;Card.Body&gt;
                            &lt;div style={{ textAlign: 'center' }}&gt;
                                &lt;Card.Title className=&quot;mb-4&quot;&gt;Upload Headshots&lt;/Card.Title&gt;
                            &lt;/div&gt;
                            &lt;br /&gt;
                            &lt;Card.Text&gt;
                                Please make sure to upload 15-20 high-quality images. The images should preferably be
                                from different angles, include a variety of full-face pictures from multiple angles for best results.
                            &lt;/Card.Text&gt;
                            &lt;Form.Group controlId=&quot;formFile&quot; className=&quot;mb-3&quot;&gt;
                                &lt;br /&gt;
                                &lt;Form.Control
                                    type=&quot;file&quot;
                                    onChange={onImageSelect}
                                    multiple
                                    accept=&quot;image/*&quot;
                                    className=&quot;text-center&quot;
                                /&gt;
                            &lt;/Form.Group&gt;
                            {errorMessage &amp;&amp; &lt;Alert variant=&quot;danger&quot;&gt;{errorMessage}&lt;/Alert&gt;}
                            &lt;div style={{ textAlign: 'center' }}&gt;

                                &lt;Button
                                    variant=&quot;primary&quot;
                                    onClick={handleSubmit}
                                    disabled={selectedFiles.length === 0 || selectedFiles.length &gt; 20 || loading}
                                    className=&quot;w-50&quot;
