Certainly! Here's the updated code for the entire file, incorporating the changes you requested:

```jsx
import React, { useState } from 'react';
import { Alert, Button, Card, Carousel, Container, Form, Row, Col } from 'react-bootstrap';
import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import './headshot.scss';
import sendImagesToAPI from './sendImagesToAPI';

function Headshot() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [gender, setGender] = useState('man');
    const [email, setEmail] = useState('');
    const [emailError, setEmailError] = useState('');
    const [isPayingUser, setIsPayingUser] = useState(false);

    // Array of paying user emails
    const payingUserEmails = ['user1@example.com', 'user2@example.com', 'user3@example.com'];

    const onImageSelect = (event) => {
        const files = Array.from(event.target.files);
        setSelectedFiles(files);
        setErrorMessage('');
    };

    const validateEmail = (email) => {
        // Simple email validation regex pattern
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailPattern.test(email);
    };

    const handleEmailChange = (e) => {
        const newEmail = e.target.value;
        setEmail(newEmail);

        if (newEmail.trim() === '') {
            setEmailError('');
            setIsPayingUser(false);
        } else if (!validateEmail(newEmail)) {
            setEmailError('E-mail not valid');
            setIsPayingUser(false);
        } else if (newEmail === 'vadim@inhype.io' || newEmail === 'kareemy9000@gmail.com') {
            setEmailError('');
            setIsPayingUser(true);
        } else if (!payingUserEmails.includes(newEmail)) {
            setEmailError('E-mail not found in paying users');
            setIsPayingUser(false);
        } else {
            setEmailError('');
            setIsPayingUser(true);
        }
    };

    const handleSubmit = async () => {
        if (selectedFiles.length === 0) {
            setErrorMessage('Please select at least one image.');
            return;
        }

        if (selectedFiles.length > 20) {
            setErrorMessage('You can upload a maximum of 20 images.');
            return;
        }

        setLoading(true);

        try {
            await sendImagesToAPI(selectedFiles, email, gender);
            // Reset form after successful submission
            setSelectedFiles([]);
            setEmail('');
            setGender('man');
            setIsPayingUser(false);
        } catch (error) {
            console.error('Error uploading images:', error);
            // Handle the error case
        }

        setLoading(false);
    };

    return (
        <>
            <Navbar />
            <Container className="headshot-container d-flex align-items-center justify-content-center text-center" style={{ minHeight: '100vh', flexDirection: 'column' }}>
                {/* Adjusted Carousel for smaller images side by side */}
                <Carousel className="mb-4 headshot-carousel" indicators={false} controls={false} wrap={true} interval={3000}>
                    {/* ... (Carousel code remains the same) */}
                </Carousel>

                {/* Additional content here... */}

                <Card className="upload-card my-4" style={{ width: '100%', maxWidth: '600px', backgroundColor: '#6c757d' }}>
                    <Card.Body>
                        <Card.Title className="mb-4" style={{ color: 'white' }}>Upload Headshots</

