        return &lt;p&gt;Loading...&lt;/p&gt;;
    }
    if (!user) {
        return &lt;Navigate to=&quot;/home&quot; /&gt;;
    }
    return children;
};
ProtectedRoute.propTypes = {
    children: PropTypes.node.isRequired
};
export default ProtectedRoute;

