                    disabled={selectedFiles.length === 0 || selectedFiles.length &gt; 15 || loading || !email || emailError || !isPayingUser || !gender}
                    className=&quot;mt-2&quot;
                    style={{
                        backgroundColor: '#747fe7',
                        borderColor: '#14b0e2',
                        borderRadius: '25px',
                        padding: '10px 30px',
                        color: 'black'
                    }}
                &gt;
                    {loading ? 'Processing...' : 'Submit'}
                &lt;/Button&gt;
            &lt;/Container&gt;
            &lt;Footer/&gt;
        &lt;/&gt;
        );
}

