import React, {useState, useEffect} from 'react';
import {Alert, Button, Card, Carousel, Container, Form} from 'react-bootstrap';
import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import './headshot.scss';


import { storage } from '../../firebaseauth.js';
import {ref, uploadBytes ,  listAll, getDownloadURL} from 'firebase/storage';


function Headshot() {

        const [selectedFiles, setSelectedFiles] = useState([]);
        const [errorMessage, setErrorMessage] = useState('');
        const [loading, setLoading] = useState(false);
        const [gender, setGender] = useState('man');
        const [email, setEmail] = useState('');
        const [emailError, setEmailError] = useState('');
        const [isPayingUser, setIsPayingUser] = useState(false);
        const [imageUrls, setImageUrls] = useState([]);


    // Array of paying user emails
        const payingUserEmails = ['kareemy9000@gmail.com', 'vadim@inhype.io'];

        const onImageSelect = (event) =&gt; {
            const files = Array.from(event.target.files);
            setSelectedFiles(files);
            setErrorMessage('');
        };
        const validateEmail = (email) =&gt; {
            // Simple email validation regex pattern
            const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            return emailPattern.test(email);
        };
        const handleEmailChange = (e) =&gt; {
            const newEmail = e.target.value;
            setEmail(newEmail);
            if (newEmail.trim() === '') {
                setEmailError('');
                setIsPayingUser(false);
            } else if (!validateEmail(newEmail)) {
                setEmailError('E-mail not valid');
                setIsPayingUser(false);
            } else if (!payingUserEmails.includes(newEmail)) {
                setEmailError('E-mail not found in paying users');
                setIsPayingUser(false);
            } else {
                setEmailError('');
                setIsPayingUser(true);
            }
        };
    const handleSubmit = async (e) =&gt; {
        e.preventDefault();
        if (selectedFiles.length &lt; 5) {
            setErrorMessage('Please select at least 5 images.');
            return;
        }
        if (selectedFiles.length &gt; 15) {
            setErrorMessage('You can upload a maximum of 15 images.');
            return;
        }
        if (selectedFiles.length &gt; 0 &amp;&amp; gender) {
            const uploadPromises = selectedFiles.map((file) =&gt; {
                const storageRef = ref(storage, file.name);
                return uploadBytes(storageRef, file);
            });
            try {
                await Promise.all(uploadPromises);
                console.log('Images uploaded successfully');
                alert('Images uploaded successfully.');
                // Reset the form after successful upload
                setGender('');
                setSelectedFiles([]);
            } catch (err) {
                console.error('Error uploading images:', err);
                alert('Error uploading images. Please try again.');
            }
        }
        setLoading(true);
    };
    //
    // if (selectedImage &amp;&amp; gender) {
    //     const S3_BUCKET = 'training-cherie';
    //     const REGION = 'us-east-1';
    //
    //     const s3Client = new S3Client({
    //         region: REGION,
    //         credentials: {
    //             accessKeyId: 'your-access-key',
    //             secretAccessKey: 'your-secret-access-key',
    //         },
    //     });
    // }
        // try {
        //     // Create a folder with the user's email in the S3 bucket
        //     const folderKey = `${email}/`;
        //
        //     // Upload each image to the user's folder in the S3 bucket
        //     const uploadPromises = selectedFiles.map((file, index) =&gt; {
        //         const fileName = `image_${index + 1}.jpg`;
        //         const fileKey = `${folderKey}${fileName}`;
        //         return s3.upload({
        //             Bucket: 'training-cheri',
        //             Key: fileKey,
        //             Body: file,
        //             ContentType: file.type,
        //         }).promise();
        //     });
        //
        //     await Promise.all(uploadPromises);
        //
        //     // Reset form after successful submission
        //     setSelectedFiles([]);
        //     setEmail('');
        //     setGender('man');
        //     setIsPayingUser(false);
        // } catch (error) {
        //     console.error('Error uploading images:', error);
        //     // Handle the error case
        // }
        //
        // setLoading(false);

    const fetchImageUrls = async () =&gt; {
        const folderRef = ref(storage, 'Samples/');
        try {
            const listResult = await listAll(folderRef);
            const urlPromises = listResult.items.map((itemRef) =&gt; getDownloadURL(itemRef));
            const urls = await Promise.all(urlPromises);
            setImageUrls(urls);
        } catch (error) {
            console.error('Error fetching image URLs:', error);
        }
    };

    useEffect(() =&gt; {
        fetchImageUrls();
    }, []);

        return (
        &lt;&gt;
            &lt;Navbar /&gt;
            &lt;Container className=&quot;headshot-container d-flex align-items-center justify-content-center text-center&quot;
                       style={{minHeight: '100vh', flexDirection: 'column'}}&gt;
                &lt;Carousel
                    className=&quot;mb-4 headshot-carousel inline-carousel&quot;
                    indicators={false}
                    controls={false}
                    wrap={true}
                    interval={3000}
                &gt;
                    {imageUrls.map((url, index) =&gt; (
                        &lt;Carousel.Item key={index} className=&quot;inline-carousel-item&quot;&gt;
                            &lt;img className=&quot;d-inline-block w-auto custom-img&quot; src={url} alt={`Image ${index + 1}`}/&gt;
                        &lt;/Carousel.Item&gt;
                    ))}
                &lt;/Carousel&gt;

                &lt;div className=&quot;text-center mb-5&quot;&gt;
                    &lt;h2 style={{color: 'purple', fontWeight: 'bold'}}&gt;Photorealistic Generated Headshots&lt;/h2&gt;
                &lt;/div&gt;

                {/* Additional content here... */}
                {/* Email form */}
                &lt;Card className=&quot;email-card my-4&quot; style={{width: '100%', maxWidth: '600px', backgroundColor: '#FFFF'}}&gt;
                    &lt;Card.Body&gt;
                        &lt;Card.Title className=&quot;mb-4&quot; style={{color: 'black'}}&gt;Enter Your Email&lt;/Card.Title&gt;
                        &lt;Form.Group controlId=&quot;emailInput&quot;&gt;
                            &lt;Form.Control
                                type=&quot;email&quot;
                                placeholder=&quot;Enter your email&quot;
                                value={email}
                                onChange={handleEmailChange}
                                style={{borderRadius: '25px', padding: '10px 20px', color: 'black'}}
                            /&gt;
                            {emailError &amp;&amp; (
                                &lt;Form.Text style={{color: 'red', marginTop: '8px'}}&gt;{emailError}&lt;/Form.Text&gt;
                            )}
                        &lt;/Form.Group&gt;
                    &lt;/Card.Body&gt;
                &lt;/Card&gt;


                &lt;Card className=&quot;upload-card my-4&quot; style={{width: '100%', maxWidth: '600px'}}&gt;
                    &lt;Card.Body&gt;
                        &lt;Card.Title className=&quot;mb-4&quot;&gt;Upload Images&lt;/Card.Title&gt;
                        &lt;br/&gt;
                        &lt;Card.Text&gt;
                            Please make sure to upload 10-15 high-quality images. &lt;br/&gt;&lt;br/&gt; The images should
                            preferably be from different angles for best results.
                        &lt;/Card.Text&gt;


                        &lt;Form.Group controlId=&quot;formFile&quot; className=&quot;mb-3&quot;&gt;
                            &lt;Form.Control type=&quot;file&quot; onChange={onImageSelect} multiple accept=&quot;image/*&quot;/&gt;
                        &lt;/Form.Group&gt;
                        {errorMessage &amp;&amp; &lt;Alert variant=&quot;danger&quot;&gt;{errorMessage}&lt;/Alert&gt;}
                    &lt;/Card.Body&gt;
                &lt;/Card&gt;

                &lt;Card.Text&gt;
                    &lt;b&gt;Good examples of uploaded images:&lt;/b&gt;
                    &lt;br/&gt;


                    &lt;div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between'}}&gt;
                        &lt;div style={{textAlign: 'center', width: '100%', marginBottom: '20px'}}&gt;
                            &lt;span&gt; Face and neck should not be covered.&lt;/span&gt;
                            &lt;div style={{
                                width: '100%',
                                maxWidth: '250px',
                                height: '200px',
                                backgroundColor: '#f0f0f0',
                                margin: '10px auto'
                            }}&gt;
                                &lt;img
                                    src=&quot;http://training-cheri.s3-website-us-east-1.amazonaws.com/56.jpg&quot;
                                    alt=&quot;Face and neck not covered&quot;
