import React, {useState, useEffect} from 'react';
import {Alert, Button, Card, Carousel, Container, Form} from 'react-bootstrap';
import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';


import { storage } from '../../firebaseauth.js';
import {ref, uploadBytes, listAll, getDownloadURL, getStorage} from 'firebase/storage';
import './headshot.scss';
import {Typography} from &quot;@mui/joy&quot;;

function Headshot() {

        const [selectedFiles, setSelectedFiles] = useState([]);
        const [errorMessage, setErrorMessage] = useState('');
        const [loading, setLoading] = useState(false);
        const [gender, setGender] = useState('man');
        const [email, setEmail] = useState('');
        const [emailError, setEmailError] = useState('');
        const [isPayingUser, setIsPayingUser] = useState(false);
        const [imageUrls, setImageUrls] = useState([]);
        const [checkboxes, setCheckboxes] = useState([false, false, false, false]);
        const [showError, setShowError] = useState(false);
    const [imageUrl, setImageUrl] = useState(&quot;&quot;); //for sample images
    const [homeImageUrls, setHomeImageUrls] = useState({});
        const handleCheckboxChange = (index) =&gt; {
            const updatedCheckboxes = [...checkboxes];
            updatedCheckboxes[index] = !updatedCheckboxes[index];
            setCheckboxes(updatedCheckboxes);
            setShowError(false);
        };

    // Array of paying user email
        const payingUserEmails = ['kareemy9000@gmail.com', 'vadim@inhype.io', 'athul.j@gmail.com'];

        const onImageSelect = (event) =&gt; {
            const files = Array.from(event.target.files);
            setSelectedFiles(files);
            setErrorMessage('');
        };
        const validateEmail = (email) =&gt; {
            // Simple email validation regex pattern
            const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            return emailPattern.test(email);
        };
        const handleEmailChange = (e) =&gt; {
            const newEmail = e.target.value;
            setEmail(newEmail);
            if (newEmail.trim() === '') {
                setEmailError('');
                setIsPayingUser(false);
            } else if (!validateEmail(newEmail)) {
                setEmailError('E-mail not valid');
                setIsPayingUser(false);
            } else if (!payingUserEmails.includes(newEmail)) {
                setEmailError('E-mail not found in paying users');
                setIsPayingUser(false);
            } else {
                setEmailError('');
                setIsPayingUser(true);
            }
        };


    const handleSubmit = async (e) =&gt; {
        e.preventDefault();
        if (selectedFiles.length &lt; 5) {
            setErrorMessage('Please select at least 5 images.');
            return;
        }
        if (selectedFiles.length &gt; 20) {
            setErrorMessage('You can upload a maximum of 20 images.');
            return;
        }
        if (selectedFiles.length &gt; 0 &amp;&amp; gender) {
            setLoading(true);

            const folderName = email;
            const uploadPromises = selectedFiles.map((file, index) =&gt; {
                const fileExtension = file.name.split('.').pop();
                const newFileName = `${index + 1}.${fileExtension}`; // Rename the file to a number (e.g., 1.png, 2.png)
                const storageRef = ref(storage, `${folderName}/${newFileName}`);
                return uploadBytes(storageRef, file);
            });

            try {
                await Promise.all(uploadPromises);
                console.log('Images uploaded successfully');
                alert('Images uploaded successfully.');
                // Reset the form after successful upload
                setGender('');
                setSelectedFiles([]);
            } catch (err) {
                console.error('Error uploading images to pineapply:', err);
                alert('Error uploading images. Please try again.');
            }
        }
        setLoading(false); // Set loading back to false after the upload process is complete
    };

    const fetchImageUrls = async () =&gt; {
        const folderRef = ref(storage, 'Samples/');
        try {
            const listResult = await listAll(folderRef);
            const urlPromises = listResult.items.map((itemRef) =&gt; getDownloadURL(itemRef));
            const urls = await Promise.all(urlPromises);
            setImageUrls(urls);
        } catch (error) {
            console.error('Error fetching SAMPLE AI image URLs:', error);
        }
    };
    const handleSelect = (selectedIndex, e) =&gt; {
        setIndex(selectedIndex);
    };
    useEffect(() =&gt; {
        const storage = getStorage();
        const imageNames = ['1.jpg', '2.png', '3.png'];

        const fetchHomeImageUrls = async () =&gt; {
            const urls = {};
            for (const imageName of imageNames) {
                const imageRef = ref(storage, `/home/${imageName}`);
                try {
                    const url = await getDownloadURL(imageRef);
                    urls[imageName] = url;
                } catch (error) {
                    console.error(`Error getting download URL for ${imageName}:`, error);
                }
            }
            setHomeImageUrls(urls);
        };

        fetchImageUrls(); // Fetch sample image URLs
        fetchHomeImageUrls(); // Fetch specific home image URLs
    }, []);

        return (
        &lt;&gt;
            &lt;Navbar /&gt;
            &lt;div className=&quot;page-container&quot;&gt;
                &lt;Container className=&quot;headshot-container d-flex align-items-center justify-content-center text-center&quot;&gt;

                    &lt;div className=&quot;text-center mb-5&quot;&gt;
                        &lt;Typography
                            variant=&quot;h2&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;h1&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.8rem', sm: '3rem', md: '4rem'},
                                marginBottom: '1rem',
                                padding: '1rem',
                                boxSizing: 'border-box',
                                textAlign: 'center',
                                width: '100%',
                            }}
                        &gt;
                            Photorealistic Generated Headshots
                        &lt;/Typography&gt;

                        &lt;br/&gt;

                    &lt;/div&gt;
                    &lt;div className=&quot;headshot-grid&quot;&gt;
                        {imageUrls.map((url, i) =&gt; (
                            &lt;div key={i} className=&quot;headshot-item&quot;&gt;
                                &lt;img src={url} alt={`Headshot ${i + 1}`}/&gt;
                            &lt;/div&gt;
                        ))}
                    &lt;/div&gt;

                    &lt;p&gt; AI-generated professional portraits made through the pineapply ai headshot generator. AI can be
                        a great solution
                        for your headshots, &lt;b&gt; but you do need to upload good input images for the best results.&lt;/b&gt;
                    &lt;/p&gt;
                    &lt;Card.Text&gt;
                        &lt;Typography
                            variant=&quot;h2&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;h1&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.8rem', sm: '3rem', md: '4rem'},
                                marginBottom: '1rem',
                                padding: '1rem',
                                boxSizing: 'border-box',
                                textAlign: 'center',
                                width: '100%',
                            }}
                        &gt;
                            Good examples of uploaded images: &lt;/Typography&gt;


                        &lt;br/&gt;

                        &lt;div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'center'}}&gt;
                            {Object.entries(homeImageUrls).map(([imageName, url]) =&gt; (
                                &lt;div
                                    key={imageName}
                                    style={{
                                        textAlign: 'center',
                                        width: '100%',
                                        maxWidth: '300px',
                                        marginBottom: '20px',
                                        margin: '0 auto',
                                    }}
                                &gt;
                                    &lt;span&gt; {imageName}&lt;/span&gt;
                                    &lt;div
                                        style={{
                                            width: '100%',
                                            height: '300px',
                                            backgroundColor: '#f0f0f0',
                                            margin: '10px auto',
                                        }}
                                    &gt;
                                        {url &amp;&amp; (
                                            &lt;img
                                                src={url}
                                                alt={imageName}
                                                style={{width: '100%', height: '100%', objectFit: 'cover'}}
                                            /&gt;
                                        )}
                                    &lt;/div&gt;
                                &lt;/div&gt;
                            ))}
                        &lt;/div&gt;
                    &lt;/Card.Text&gt;

                    &lt;Card className=&quot;upload-card my-4&quot; style={{width: '100%', maxWidth: '800px'}}&gt;
                        &lt;Card.Body&gt;
                            &lt;Card.Title className=&quot;mb-4&quot;&gt;Upload Images&lt;/Card.Title&gt;
                            &lt;div style={{marginBottom: '1rem'}}&gt;&lt;/div&gt;
                            {/* Added spacing */}
                            &lt;div className=&quot;d-flex flex-column align-items-start&quot;&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox1&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        I have selected 5-20 high-quality images.
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox1&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox2&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        My images have variety in backgrounds (not the same background for all of
                                        them) &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox2&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox3&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        My images are not repeated, my images are in different clothing and
                                        environments.
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox3&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox4&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        I am the only person in my photos &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox4&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox5&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        My face and neck are not covered in any images.
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox5&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;
                            &lt;div className=&quot;d-flex flex-column align-items-center mb-3&quot;&gt;
                                &lt;Form.Group controlId=&quot;formFile&quot;&gt;
                                    &lt;Form.Label className=&quot;custom-file-upload&quot;&gt;
                                        &lt;i className=&quot;fas fa-cloud-upload-alt me-2&quot;&gt;&lt;/i&gt;
                                        Choose Files
                                        &lt;Form.Control type=&quot;file&quot; onChange={onImageSelect} multiple accept=&quot;image/*&quot;
                                                      className=&quot;d-none&quot;/&gt;
                                    &lt;/Form.Label&gt;
                                &lt;/Form.Group&gt;
                                {errorMessage &amp;&amp; &lt;div className=&quot;text-danger text-center mt-2&quot;&gt;{errorMessage}&lt;/div&gt;}
                            &lt;/div&gt;
                        &lt;/Card.Body&gt;
                    &lt;/Card&gt;

