const Home = () =&gt; {
    const [imageUrls, setImageUrls] = useState([]);
    const [bannerUrl, setBannerUrl] = useState('');


    useEffect(() =&gt; {
        const fetchImageUrls = async () =&gt; {
            const storage = getStorage();
            const urls = [];
            try {
                for (let i = 1; i &lt;= 4; i++) {
                    const fileRef = ref(storage, `${i}card.jpg`);
                    const url = await getDownloadURL(fileRef);
                    urls.push(url);
                }
                setImageUrls(urls);
            } catch (error) {
                console.error('Error fetching image URLs:', error);
            }
        };
        const fetchBannerUrl = async () =&gt; {
            const storage = getStorage();
            const bannerRef = ref(storage, 'robot_yellow.png');
            try {
                const url = await getDownloadURL(bannerRef);
                setBannerUrl(url);
            } catch (error) {
                console.error('Error fetching banner URL:', error);
            }
        };
        fetchImageUrls();
        fetchBannerUrl();

    }, []);


    const fetchImageUrls = async () =&gt; {
        const folderRef = ref(storage, 'Samples/');
        try {
            const listResult = await listAll(folderRef);
            const urlPromises = listResult.items.map((itemRef) =&gt; getDownloadURL(itemRef));
            const urls = await Promise.all(urlPromises);
            setImageUrls(urls);
        } catch (error) {
            console.error('Error fetching image URLs:', error);
        }
