import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';

import { storage } from '../../firebaseauth.js';
import {ref, uploadBytes, listAll, getDownloadURL, getStorage} from 'firebase/storage';
import './headshot.scss';
import {Typography} from &quot;@mui/joy&quot;;

function Headshot() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [gender, setGender] = useState('man');
    const [email, setEmail] = useState('');
    const [emailError, setEmailError] = useState('');
    const [isPayingUser, setIsPayingUser] = useState(false);
    const [imageUrls, setImageUrls] = useState([]);
    const [checkboxes, setCheckboxes] = useState([false, false, false, false]);
    const [showError, setShowError] = useState(false);

    const [homeImageUrls, setHomeImageUrls] = useState({});
    const [homeImageTexts, setHomeImageTexts] = useState({
        '1.jpg': ' Clear face and only you in the photo.',
        '2.png': ' High quality images with variety in backgrounds.',
        '3.png': ' Different clothing and environments',
        '4.jpg': ' Only one person in the photo',
        '5.png': ' Face and neck not covered',
        '6.png': ' High-quality images'
    });

    const handleCheckboxChange = (index) =&gt; {
        const updatedCheckboxes = [...checkboxes];
        updatedCheckboxes[index] = !updatedCheckboxes[index];
        setCheckboxes(updatedCheckboxes);
        setShowError(false);
    };

    // Array of paying user email
    const payingUserEmails = ['kareemy9000@gmail.com', 'vadim@inhype.io', 'athul.j@gmail.com'];

    const onImageSelect = (event) =&gt; {
        const files = Array.from(event.target.files);
        setSelectedFiles(files);
        setErrorMessage('');
    };

    const validateEmail = (email) =&gt; {
        // Simple email validation regex pattern
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailPattern.test(email);
    };

    const handleEmailChange = (e) =&gt; {
        const newEmail = e.target.value;
        setEmail(newEmail);
        if (newEmail.trim() === '') {
            setEmailError('');
            setIsPayingUser(false);
        } else if (!validateEmail(newEmail)) {
            setEmailError('E-mail not valid');
            setIsPayingUser(false);
        } else if (!payingUserEmails.includes(newEmail)) {
            setEmailError('E-mail not found in paying users');
            setIsPayingUser(false);
        } else {
            setEmailError('');
            setIsPayingUser(true);
        }
    };

    const handleSubmit = async (e) =&gt; {
        e.preventDefault();
        if (selectedFiles.length &lt; 5) {
            setErrorMessage('Please select at least 5 images.');
            return;
        }
        if (selectedFiles.length &gt; 20) {
            setErrorMessage('You can upload a maximum of 20 images.');
            return;
        }
        if (selectedFiles.length &gt; 0 &amp;&amp; gender) {
            setLoading(true);

            const folderName = email;
            const uploadPromises = selectedFiles.map((file, index) =&gt; {
                const fileExtension = file.name.split('.').pop();
                const newFileName = `${index + 1}.${fileExtension}`; // Rename the file to a number (e.g., 1.png, 2.png)
                const storageRef = ref(storage, `${folderName}/${newFileName}`);
                return uploadBytes(storageRef, file);
            });

            try {
                await Promise.all(uploadPromises);
                console.log('Images uploaded successfully');
                alert('Images uploaded successfully.');
                // Reset the form after successful upload
                setGender('');
                setSelectedFiles([]);
            } catch (err) {
                console.error('Error uploading images to pineapply:', err);
                alert('Error uploading images. Please try again.');
            }
        }
        setLoading(false); // Set loading back to false after the upload process is complete
    };

    const fetchImageUrls = async () =&gt; {
        const folderRef = ref(storage, 'Samples/');
        try {
            const listResult = await listAll(folderRef);
            const urlPromises = listResult.items.map((itemRef) =&gt; getDownloadURL(itemRef));
            const urls = await Promise.all(urlPromises);
            setImageUrls(urls);
        } catch (error) {
            console.error('Error fetching SAMPLE AI image URLs:', error);
        }
    };

    const handleSelect = (selectedIndex, e) =&gt; {
        setIndex(selectedIndex);
    };

    useEffect(() =&gt; {
        const storage = getStorage();
        const imageNames = ['1.jpg', '2.png', '3.png', '4.jpg', '5.png', '6.png'];

        const fetchHomeImageUrls = async () =&gt; {
            const urls = {};
            for (const imageName of imageNames) {
                const imageRef = ref(storage, `/home/${imageName}`);
                try {
                    const url = await getDownloadURL(imageRef);
                    urls[imageName] = url;
                } catch (error) {
                    console.error(`Error getting download URL for ${imageName}:`, error);
                }
            }
            setHomeImageUrls(urls);
        };

        fetchImageUrls(); // Fetch sample image URLs
        fetchHomeImageUrls(); // Fetch specific home image URLs
    }, []);

    return (
        &lt;&gt;
            &lt;Navbar /&gt;
            &lt;div className=&quot;page-container&quot;&gt;
                &lt;Container className=&quot;headshot-container d-flex align-items-center justify-content-center text-center&quot;&gt;
                    &lt;div className=&quot;text-center mb-5&quot;&gt;
                        &lt;Typography
                            variant=&quot;h2&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;h1&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.8rem', sm: '3rem', md: '4rem'},
                                marginBottom: '1rem',
                                padding: '1rem',
                                boxSizing: 'border-box',
                                textAlign: 'center',
                                width: '100%',
                            }}
                        &gt;
                            Photorealistic Generated Headshots
                        &lt;/Typography&gt;
                        &lt;br/&gt;
                    &lt;/div&gt;
                    &lt;div className=&quot;headshot-grid&quot;&gt;
                        {imageUrls.map((url, i) =&gt; (
                            &lt;div key={i} className=&quot;headshot-item&quot;&gt;
                                &lt;img src={url} alt={`Headshot ${i + 1}`}/&gt;
                            &lt;/div&gt;
                        ))}
                    &lt;/div&gt;
                    &lt;div style={{textAlign: 'center', marginBottom: '2rem'}}&gt;
                        &lt;Typography
                            variant=&quot;h5&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;p&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.2rem', sm: '1.4rem', md: '1.6rem'},
                                lineHeight: '1.5',
                            }}
                        &gt;
                            AI-generated professional portraits
                            &lt;br/&gt;
                            made through the pineapply AI headshot generator. The face in your output will mostly resemble the face used in your uploaded images
                        &lt;/Typography&gt;
                        &lt;Typography
                            variant=&quot;body1&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;p&quot;
                            sx={{
                                fontSize: {xs: '1rem', sm: '1.2rem', md: '1.4rem'},
                                lineHeight: '1.5',
                                fontWeight: 'bold',
                            }}
                        &gt;
                            AI can be a great solution for your headshots,but you do need to upload good input images for the best results.
                            &lt;br/&gt;
                            &lt;span style={{fontWeight: 'normal'}}&gt;
    &lt;/span&gt;
                        &lt;/Typography&gt;
                        &lt;Typography
                            variant=&quot;h6&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;p&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.2rem', sm: '1.4rem', md: '1.6rem'},
                                lineHeight: '1.5',
                                marginTop: '1rem',
                            }}
                        &gt;
                        &lt;/Typography&gt;
                    &lt;/div&gt;

                    &lt;Card.Text&gt;
                        &lt;Typography
                            variant=&quot;h2&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;h1&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.8rem', sm: '3rem', md: '4rem'},
                                marginBottom: '1rem',
                                padding: '1rem',
                                boxSizing: 'border-box',
                                textAlign: 'center',
                                width: '100%',
                            }}
                        &gt;
                            Good examples of uploaded images:
                        &lt;/Typography&gt;
                        &lt;br/&gt;

                        &lt;div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'center'}}&gt;
                            {Object.entries(homeImageUrls).map(([imageName, url]) =&gt; (
                                &lt;div
                                    key={imageName}
                                    style={{
                                        textAlign: 'center',
                                        width: '100%',
                                        maxWidth: '300px',
                                        marginBottom: '20px',
                                        margin: '0 auto',
                                    }}
                                &gt;
                                    &lt;span&gt;{homeImageTexts[imageName]}&lt;/span&gt;
                                    &lt;div
                                        style={{
                                            width: '100%',
                                            height: '300px',
                                            backgroundColor: '#f0f0f0',
                                            margin: '10px auto',
                                        }}
                                    &gt;
                                        {url &amp;&amp; (
                                            &lt;img
                                                src={url}
                                                alt={imageName}
                                                style={{width: '100%', height: '100%', objectFit: 'cover'}}
                                            /&gt;
                                        )}
                                    &lt;/div&gt;
                                &lt;/div&gt;
                            ))}
                        &lt;/div&gt;
                    &lt;/Card.Text&gt;

                    &lt;Card className=&quot;upload-card my-4&quot; style={{width: '100%', maxWidth: '800px', borderRadius: '25px'}}&gt;
                        &lt;Card.Body&gt;
                            &lt;Card.Title className=&quot;mb-4&quot;&gt;Upload Images&lt;/Card.Title&gt;
                            &lt;div style={{marginBottom: '1rem'}}&gt;&lt;/div&gt;
                            {/* Added spacing */}
                            &lt;div className=&quot;d-flex flex-column align-items-start&quot;&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox1&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        I have selected 5-20 high-quality images.
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox1&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox2&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        My images have variety in backgrounds (not the same background for all of
                                        them)
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox2&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox3&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        My images are not repeated, my images are in different clothing and
                                        environments.
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox3&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox4&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        I am the only person in my photos
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox4&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                                &lt;div className=&quot;d-flex align-items-center mb-3&quot; style={{marginBottom: '1.5rem'}}&gt;
                                    &lt;label htmlFor=&quot;checkbox5&quot; className=&quot;flex-grow-1 me-3&quot;&gt;
                                        My face and neck are not covered in any images.
                                    &lt;/label&gt;
                                    &lt;input type=&quot;checkbox&quot; id=&quot;checkbox5&quot; className=&quot;form-check-input&quot;/&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;
                            &lt;div className=&quot;d-flex flex-column align-items-center mb-3&quot;&gt;
                                &lt;Form.Group controlId=&quot;formFile&quot;&gt;
                                    &lt;Form.Label className=&quot;custom-file-upload&quot;&gt;
                                        &lt;i className=&quot;fas fa-cloud-upload-alt me-2&quot;&gt;&lt;/i&gt;
                                        Choose Files
                                        &lt;Form.Control type=&quot;file&quot; onChange={onImageSelect} multiple accept=&quot;image/*&quot;
                                                      className=&quot;d-none&quot;/&gt;
                                    &lt;/Form.Label&gt;
                                &lt;/Form.Group&gt;
                                {errorMessage &amp;&amp; &lt;div className=&quot;text-danger text-center mt-2&quot;&gt;{errorMessage}&lt;/div&gt;}
                            &lt;/div&gt;
                        &lt;/Card.Body&gt;
                    &lt;/Card&gt;

                    &lt;Card className=&quot;email-card my-4&quot;
                          style={{width: '100%', maxWidth: '600px', backgroundColor: 'transparent'}}&gt;
                        &lt;Card.Body&gt;
                            &lt;Card.Title className=&quot;mb-4-email&quot; style={{color: 'black'}}&gt;
                                Enter Your Email
                            &lt;/Card.Title&gt;
                            &lt;Form.Group controlId=&quot;emailInput&quot;&gt;
                                &lt;Form.Control
                                    type=&quot;email&quot;
                                    placeholder=&quot;Enter your email&quot;
                                    value={email}
                                    onChange={handleEmailChange}
                                    style={{borderRadius: '25px', padding: '10px 20px', color: 'black'}}
                                /&gt;
                                {emailError &amp;&amp; &lt;Form.Text style={{ color: 'red', marginTop: '8px', display: 'block' }}&gt;{emailError}&lt;/Form.Text&gt;}
                            &lt;/Form.Group&gt;
                        &lt;/Card.Body&gt;
                    &lt;/Card&gt;

                    &lt;Form.Group controlId=&quot;genderSelect&quot; className=&quot;mb-3&quot; style={{display: 'inline-block'}}&gt;
                        &lt;Form.Label&gt;Select Your Gender&lt;/Form.Label&gt;
                        &lt;Form.Select value={gender} onChange={(e) =&gt; setGender(e.target.value)}
                                     style={{width: 'auto'}}&gt;
                            &lt;option value=&quot;&quot;&gt;Choose...&lt;/option&gt;
                            &lt;option value=&quot;man&quot;&gt;Man&lt;/option&gt;
                            &lt;option value=&quot;woman&quot;&gt;Woman&lt;/option&gt;
                        &lt;/Form.Select&gt;
                    &lt;/Form.Group&gt;

                    &lt;Button
                        variant=&quot;primary&quot;
                        onClick={handleSubmit}
                        disabled={selectedFiles.length === 0 || selectedFiles.length &lt; 5 || selectedFiles.length &gt; 20 || loading || !email || emailError || !isPayingUser || !gender}
                        className=&quot;mt-2 submit-button&quot;
                    &gt;
                        {loading ? 'Processing...' : 'Submit'}
                    &lt;/Button&gt;
                    &lt;Footer/&gt;
                &lt;/Container&gt;
            &lt;/div&gt;
        &lt;/&gt;
    );
}

export default Headshot;

CHECK THIS FILE, CAN YOU DISPLAY &quot;errormessage&quot; the one from seterrormessage to the user on the page? I want it to display (if the user uploads less than 5 images or more than 20)

Sure, you can display the `errorMessage` to the user on the page by adding a conditional rendering for an error message component or element. Here's an example of how you can modify the code to achieve this:

```jsx
<Container className="headshot-container d-flex align-items-center justify-content-center text-center">
    {/* ... */}

    <Card className="upload-card my-4" style={{ width: '100%', maxWidth: '800px', borderRadius: '25px' }}>
        <Card.Body>
            <Card.Title className="mb-4">Upload Images</Card.Title>
            {/* Added spacing */}
            <div style={{ marginBottom: '1rem' }}></div>
            {/* Display error message if present */}
            {errorMessage && (
                <Alert variant="danger" className="text-center">
                    {errorMessage}
                </Alert>
            )}
            {/* ... */}
        </Card.Body>
    </Card>

    {/* ... */}
</Container>
```

In the above code, we added a conditional rendering for an `Alert` component from `react-bootstrap` inside the `Card` component where the file upload happens. If `errorMessage` has a value (i.e., not an empty string), it will render the `Alert` component with the `variant="danger"` prop, which will display the error message in a red alert box.

You can style the `Alert` component further by adding additional CSS classes or inline styles as per your requirements.

Alternatively, if you prefer not to use the `Alert` component from `react-bootstrap`, you can create a custom error message element using regular HTML and CSS. For example:

```jsx
{errorMessage && (
    <div className="error-message text-center text-danger mb-3">
        {errorMessage}
    </div>
)}
```

This will render a `div` element with the `error-message` class and the `text-center`, `text-danger`, and `mb-3` (margin-bottom) utility classes from Bootstrap when `errorMessage` is not an empty string. You can then style the `.error-message` class in your CSS file to customize the appearance of the error message.

To show a message when not all checkboxes are checked, you can add a condition to check if all checkboxes are checked before allowing the form submission. Here's how you can modify the code:

```jsx
// ...

const handleSubmit = async (e) => {
    e.preventDefault();
    const allChecked = checkboxes.every(Boolean); // Check if all checkboxes are true

    if (!allChecked) {
        setShowError(true);
        return;
    }

    if (selectedFiles.length < 5) {
        setErrorMessage('Please select at least 5 images.');
        return;
    }

    if (selectedFiles.length > 20) {
        setErrorMessage('You can upload a maximum of 20 images.');
        return;
    }

    // ...
};

// ...

<Card className="upload-card my-4" style={{ width: '100%', maxWidth: '800px', borderRadius: '25px' }}>
    <Card.Body>
        <Card.Title className="mb-4">Upload Images</Card.Title>
        {/* Added spacing */}
        <div style={{ marginBottom: '1rem' }}></div>
        {/* Display error message if present */}
        {errorMessage && (
            <Alert variant="danger" className="text-center">
                {errorMessage}
            </Alert>
        )}
        {/* Display checkbox error message if showError is true */}
        {showError && (
            <Alert variant="danger" className="text-center">
                Checkboxes must all be checked
            </Alert>
        )}
        {/* ... */}
    </Card.Body>
</Card>

// ...
```

In the modified code:

1. We added a new state variable `showError` and initialized it to `false`.
2. In the `handleSubmit` function, we first check if all checkboxes are checked using the `every` method on the `checkboxes` array. If not all checkboxes are checked, we set `showError` to `true` and return from the function.
3. Inside the `Card` component, we added a new conditional rendering for an `Alert` component that displays the message "Checkboxes must all be checked" when `showError` is `true`.

Now, when the user tries to submit the form without checking all the checkboxes, the "Checkboxes must all be checked" message will be displayed. If all checkboxes are checked but there are other issues (like not enough images selected or too many images selected), the corresponding error message will be displayed instead.

Note: You might also want to reset the `showError` state when the user starts checking the checkboxes again or when the form is successfully submitted.

