                console.error('Error fetching banner URL:', error);
            }
        };
        fetchImageUrls();
        fetchBannerUrl();

    }, []);

    const handleSubmit = (e) =&gt; {
        e.preventDefault();
        // Perform form submission logic here
        setSubmitted(true);
    };

