    const baseURL = &quot;http://training-cheri.s3-website-us-east-1.amazonaws.com/samples/&quot;;
    const startIndex = 1;
    const endIndex = 6;
    const imageUrls = Array.from({ length: endIndex - startIndex + 1 }, (v, i) =&gt; `${baseURL}${i + startIndex}.png`);
    return (
        &lt;&gt;
