import React, { useState } from 'react';
import { Alert, Button, Card, Carousel, Container, Form } from 'react-bootstrap';
import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import './headshot.scss';
import { S3 } from 'aws-sdk';

// AWS SDK configuration
const s3 = new S3({
    region: 'us-east-1',
    credentials: {
        accessKeyId: 'AKIAWYBABBK5OAHAOVUX',
        secretAccessKey: '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',
    },
});

function Headshot() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [email, setEmail] = useState('');
    const [emailError, setEmailError] = useState('');
    const [isPayingUser, setIsPayingUser] = useState(false);
    const payingUserEmails = ['kareemy9000@gmail.com', 'vadim@inhype.io'];

    const onImageSelect = (event) =&gt; {
        const files = Array.from(event.target.files);
        setSelectedFiles(files);
        setErrorMessage('');
    };

    const validateEmail = (email) =&gt; {
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailPattern.test(email);
    };

    const handleEmailChange = (e) =&gt; {
        const newEmail = e.target.value;
        setEmail(newEmail);
        if (newEmail.trim() === '') {
            setEmailError('');
            setIsPayingUser(false);
        } else if (!validateEmail(newEmail)) {
            setEmailError('E-mail not valid');
            setIsPayingUser(false);
        } else if (!payingUserEmails.includes(newEmail)) {
            setEmailError('E-mail not found in paying users');
            setIsPayingUser(false);
        } else {
            setEmailError('');
            setIsPayingUser(true);
        }
    };

    const uploadImagesToS3 = async () =&gt; {
        const folderKey = `${email}/`;
        const uploadPromises = selectedFiles.map((file, index) =&gt; {
            const fileName = `image_${index + 1}.jpg`;
            const fileKey = `${folderKey}${fileName}`;
            return s3.upload({
                Bucket: 'your-s3-bucket-name', // Replace with your bucket name
                Key: fileKey,
                Body: file,
                ContentType: file.type,
            }).promise();
        });

        try {
            await Promise.all(uploadPromises);
            alert('Images successfully uploaded!');
        } catch (error) {
            console.error('Error uploading images:', error);
            setErrorMessage('Error uploading images. Please try again.');
        }
    };

    const handleSubmit = async () =&gt; {
        if (selectedFiles.length === 0 || selectedFiles.length &gt; 15) {
            setErrorMessage('Please select between 1 to 15 images.');
            return;
        }
        setLoading(true);
        await uploadImagesToS3();
        // Reset state
        setSelectedFiles([]);
        setEmail('');
        setLoading(false);
    };

    // Rest of your component's return statement remains the same...

    return (
        &lt;&gt;
            {/* Your JSX markup remains unchanged... */}
        &lt;/&gt;
    );
}

export default Headshot;

import AWS from &quot;aws-sdk&quot;;
import { useState } from &quot;react&quot;;

function App() {
  // Create state to store file
  const [file, setFile] = useState(null);

  // Function to upload file to s3
  const uploadFile = async () =&gt; {
    // S3 Bucket Name
    const S3_BUCKET = &quot;bucket-name&quot;;

    // S3 Region
    const REGION = &quot;region&quot;;

    // S3 Credentials
    AWS.config.update({
      accessKeyId: &quot;youraccesskeyhere&quot;,
      secretAccessKey: &quot;yoursecretaccesskeyhere&quot;,
    });
    const s3 = new AWS.S3({
      params: { Bucket: S3_BUCKET },
      region: REGION,
    });

    // Files Parameters

    const params = {
      Bucket: S3_BUCKET,
      Key: file.name,
      Body: file,
    };

    // Uploading file to s3

    var upload = s3
      .putObject(params)
      .on(&quot;httpUploadProgress&quot;, (evt) =&gt; {
        // File uploading progress
        console.log(
          &quot;Uploading &quot; + parseInt((evt.loaded * 100) / evt.total) + &quot;%&quot;
        );
      })
      .promise();

    await upload.then((err, data) =&gt; {
      console.log(err);
      // Fille successfully uploaded
      alert(&quot;File uploaded successfully.&quot;);
    });
  };
  // Function to handle file and store it to file state
  const handleFileChange = (e) =&gt; {
    // Uploaded file
    const file = e.target.files[0];
    // Changing file state
    setFile(file);
  };
  return (
    &lt;div className=&quot;App&quot;&gt;
      &lt;div&gt;
        &lt;input type=&quot;file&quot; onChange={handleFileChange} /&gt;
        &lt;button onClick={uploadFile}&gt;Upload&lt;/button&gt;
      &lt;/div&gt;
    &lt;/div&gt;
  );
}

export default App; 

