        &lt;&gt;
            &lt;Stack sx={{ minHeight: '100vh' }}&gt;
                &lt;Navbar /&gt;
                &lt;div id=&quot;lingo-chatbot&quot;
                     style={{flex:  1, display: 'flex', justifyContent: 'stretch', alignItems: 'stretch'}}&gt;
                    {/* The script is now loaded dynamically via useEffect */}
                &lt;/div&gt;
                &lt;Footer/&gt;
            &lt;/Stack&gt;
        &lt;/&gt;
    );
}

