function Headshot() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [gender, setGender] = useState('man');
    const [email, setEmail] = useState('');
    const [emailError, setEmailError] = useState('');
    const [isPayingUser, setIsPayingUser] = useState(false);
    const [imageUrls, setImageUrls] = useState([]);
    const [checkboxes, setCheckboxes] = useState([false, false, false, false]);
    const [showError, setShowError] = useState(false);

    const [homeImageUrls, setHomeImageUrls] = useState({});
    const [homeImageTexts, setHomeImageTexts] = useState({
        '1.jpg': ' Clear face and only you in the photo.',
        '2.png': ' High quality images with variety in backgrounds.',
        '3.png': ' Different clothing and environments',
        '4.jpg': ' Only one person in the photo',
        '5.png': ' Face and neck not covered',
        '6.png': ' High-quality images'
    });

    const handleCheckboxChange = (index) =&gt; {
        const updatedCheckboxes = [...checkboxes];
        updatedCheckboxes[index] = !updatedCheckboxes[index];
        setCheckboxes(updatedCheckboxes);
        setShowError(false);
    };

    // Array of paying user email
    const payingUserEmails = ['kareemy9000@gmail.com', 'vadim@inhype.io', 'athul.j@gmail.com'];

    const onImageSelect = (event) =&gt; {
        const files = Array.from(event.target.files);
        setSelectedFiles(files);
        setErrorMessage('');
    };

    const validateEmail = (email) =&gt; {
        // Simple email validation regex pattern
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailPattern.test(email);
    };

    const handleEmailChange = (e) =&gt; {
        const newEmail = e.target.value;
        setEmail(newEmail);
        if (newEmail.trim() === '') {
            setEmailError('');
            setIsPayingUser(false);
        } else if (!validateEmail(newEmail)) {
            setEmailError('E-mail not valid');
            setIsPayingUser(false);
        } else if (!payingUserEmails.includes(newEmail)) {
            setEmailError('E-mail not found in paying users');
            setIsPayingUser(false);
        } else {
            setEmailError('');
            setIsPayingUser(true);
        }
    };

    const handleSubmit = async (e) =&gt; {
        e.preventDefault();
        if (selectedFiles.length &lt; 5) {
            setErrorMessage('Please select at least 5 images.');
            return;
        }
        if (selectedFiles.length &gt; 20) {
            setErrorMessage('You can upload a maximum of 20 images.');
            return;
        }
        if (selectedFiles.length &gt; 0 &amp;&amp; gender) {
            setLoading(true);

            const folderName = email;
            const uploadPromises = selectedFiles.map((file, index) =&gt; {
                const fileExtension = file.name.split('.').pop();
                const newFileName = `${index + 1}.${fileExtension}`; // Rename the file to a number (e.g., 1.png, 2.png)
                const storageRef = ref(storage, `${folderName}/${newFileName}`);
                return uploadBytes(storageRef, file);
            });

            try {
                await Promise.all(uploadPromises);
                console.log('Images uploaded successfully');
                alert('Images uploaded successfully.');
                // Reset the form after successful upload
                setGender('');
                setSelectedFiles([]);
            } catch (err) {
                console.error('Error uploading images to pineapply:', err);
                alert('Error uploading images. Please try again.');
            }
        }
        setLoading(false); // Set loading back to false after the upload process is complete
    };

    const fetchImageUrls = async () =&gt; {
        const folderRef = ref(storage, 'Samples/');
        try {
            const listResult = await listAll(folderRef);
            const urlPromises = listResult.items.map((itemRef) =&gt; getDownloadURL(itemRef));
            const urls = await Promise.all(urlPromises);
            setImageUrls(urls);
        } catch (error) {
            console.error('Error fetching SAMPLE AI image URLs:', error);
        }
    };

    const handleSelect = (selectedIndex, e) =&gt; {
        setIndex(selectedIndex);
    };

    useEffect(() =&gt; {
        const storage = getStorage();
        const imageNames = ['1.jpg', '2.png', '3.png', '4.jpg', '5.png', '6.png'];

        const fetchHomeImageUrls = async () =&gt; {
            const urls = {};
            for (const imageName of imageNames) {
                const imageRef = ref(storage, `/home/${imageName}`);
                try {
                    const url = await getDownloadURL(imageRef);
                    urls[imageName] = url;
                } catch (error) {
                    console.error(`Error getting download URL for ${imageName}:`, error);
                }
            }
            setHomeImageUrls(urls);
        };

        fetchImageUrls(); // Fetch sample image URLs
        fetchHomeImageUrls(); // Fetch specific home image URLs
    }, []);

    return (
        &lt;&gt;
            &lt;Navbar /&gt;
            &lt;div className=&quot;page-container&quot;&gt;
                &lt;Container className=&quot;headshot-container d-flex align-items-center justify-content-center text-center&quot;&gt;
                    &lt;div className=&quot;text-center mb-5&quot;&gt;
                        &lt;Typography
                            variant=&quot;h2&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;h1&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.8rem', sm: '3rem', md: '4rem'},
                                marginBottom: '1rem',
                                padding: '1rem',
                                boxSizing: 'border-box',
                                textAlign: 'center',
                                width: '100%',
                            }}
                        &gt;
                            Photorealistic Generated Headshots
                        &lt;/Typography&gt;
                        &lt;br/&gt;
                    &lt;/div&gt;
                    &lt;div className=&quot;headshot-grid&quot;&gt;
                        {imageUrls.map((url, i) =&gt; (
                            &lt;div key={i} className=&quot;headshot-item&quot;&gt;
                                &lt;img src={url} alt={`Headshot ${i + 1}`}/&gt;
                            &lt;/div&gt;
                        ))}
                    &lt;/div&gt;
                    &lt;div style={{textAlign: 'center', marginBottom: '2rem'}}&gt;
                        &lt;Typography
                            variant=&quot;h5&quot;
                            color=&quot;appTheme&quot;
                            component=&quot;p&quot;
                            gutterBottom
                            sx={{
                                fontSize: {xs: '1.2rem', sm: '1.4rem', md: '1.6rem'},
                                lineHeight: '1.5',
                            }}
