import React, { useState } from 'react';
import Navbar from '../../components/Navbar/Navbar.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import axios from 'axios';
import {
    Container,
    Typography,
    TextField,
    Button,
    Paper,
    Grid,
    Box,
} from '@mui/material';

const Laborbotpage = () =&gt; {
    const [userInput, setUserInput] = useState('');
    const [chatbotResponse, setChatbotResponse] = useState('');

    const handleChatbotRequest = async () =&gt; {
        try {
            const response = await axios.post(
                'https://hope-56ogefmcva-ww.a.run.app/',
                {
                    user_question: userInput,
                }
            );
            setChatbotResponse(response.data.answer);
        } catch (error) {
            console.error('Error communicating with chatbot:', error);
            setChatbotResponse(
                'Apologies, an error occurred. Please try again later.'
            );
        }
    };

    return (
        &lt;&gt;
            &lt;Navbar /&gt;
            &lt;Container maxWidth=&quot;md&quot; sx={{ my: 4 }}&gt;
                &lt;Paper elevation={3} sx={{ p: 4 }}&gt;
                    &lt;Typography variant=&quot;h4&quot; align=&quot;center&quot; gutterBottom&gt;
                        Labor Law Chatbot
                    &lt;/Typography&gt;
                    &lt;Grid container spacing={2}&gt;
                        &lt;Grid item xs={12}&gt;
                            &lt;TextField
                                label=&quot;Ask a question&quot;
                                variant=&quot;outlined&quot;
                                fullWidth
                                value={userInput}
                                onChange={(e) =&gt; setUserInput(e.target.value)}
                            /&gt;
                        &lt;/Grid&gt;
                        &lt;Grid item xs={12}&gt;
                            &lt;Button
                                variant=&quot;contained&quot;
                                color=&quot;primary&quot;
                                fullWidth
                                onClick={handleChatbotRequest}
                            &gt;
                                Submit
                            &lt;/Button&gt;
                        &lt;/Grid&gt;
                        &lt;Grid item xs={12}&gt;
                            &lt;Box
                                sx={{
                                    bgcolor: 'background.paper',
                                    p: 2,
                                    minHeight: 200,
                                    overflowY: 'auto',
                                }}
                            &gt;
                                {chatbotResponse &amp;&amp; (
                                    &lt;Typography variant=&quot;body1&quot;&gt;
                                        {chatbotResponse}
                                    &lt;/Typography&gt;
                                )}
                            &lt;/Box&gt;
                        &lt;/Grid&gt;
                    &lt;/Grid&gt;
                &lt;/Paper&gt;
            &lt;/Container&gt;
            &lt;Footer /&gt;
        &lt;/&gt;
    );
};

export default Laborbotpage;


